import React, { Component } from 'react'

export default class FormUsingClassComponent extends Component {
    constructor(props) {
        super(props)

        this.state = {
            fullName: "",
            result: "",
            gender: 'green'
        }


    }

    handleFullName = (e) => {
        this.setState({ fullName: e.target.value });
    }

    onRadioChange = (e) => {
        this.setState({
            gender: e.target.value
        });
    }
    Display = (e) => {
        let res = `Name - ${this.state.fullName}  Gender - ${this.state.gender}`
        this.setState({ result: res });
    }

    render() {
        return (
            <>
                <form>
                    Name  :<input type="text" name="FullName" onChange={this.handleFullName} /><br />
                    <label>Gender:</label>
                    <input
                        type="radio"
                        value="male"
                        checked={this.state.gender === "male"}
                        onChange={this.onRadioChange}
                    />
                    <label> Male:</label>
                    <input
                        type="radio"
                        value="female"
                        checked={this.state.gender === "female"}
                        onChange={this.onRadioChange}
                    />
                    <label>Female</label><br/>
                    <button type="button" onClick={this.Display}>Submit</button>
                    <p>{this.state.result}</p>

                </form>
            </>
        )
    }
}
