import {useLocation} from 'react-router-dom';
import { useState } from "react";
import { useNavigate } from "react-router-dom";


const Second = () => {
    const navigate = useNavigate();
    const location = useLocation();
    
    const contact=location.state;

    const [name, setName] = useState("");
    const [wording, setWording] = useState("");


    const handleSecSubmit = (e) => {
        e.preventDefault();
        const contacttotal={
            ...contact, 
            ...{name},  
            ...{wording}
        };
        console.log(contacttotal);
        navigate("/final", { state: { contacttotal } });
    }


    return ( 
        <div className="Second">
            <h1>This is Second Input Page</h1>
            <form autoComplete="on" onSubmit={handleSecSubmit}>

                <dd>Name</dd>
                <input
                    type="text" 
                    value={name} 
                    onChange={(e) => 
                        setName(e.target.value)
                    }
                    required
                ></input>
                
                
                <dd>Wording</dd>
                <div>
                     <div onChange={(e) => setWording(e.target.value)}>
                         <label><input
                                type="radio"
                                value='United States'
                                name="wording"
                                checked={wording === "United States"}
                         />United States</label>
                         <label><input
                                 type="radio"
                                 value='British'
                                 name="wording"
                                 checked={wording === "British"}
                          />British</label>
                      </div>
                </div>
                <br/>
                <button>submit</button>
            </form>
        </div>
     );
}
 
export default Second;
