import React, { Component } from 'react'

export default class MessageClassComponent extends Component {
  constructor(props){
    super(props)
    this.state = {
        message:"Hello...."
    }
    this.messageHandler = this.messageHandler.bind(this )
  }
  messageHandler=()=>{
    this.setState({message:"Working with class Components with state and event"})

  }
    render() {
    return (
        <>
      <p>Message - {this.state.message}</p>
      <button onClick={this.messageHandler}>Change Message</button>
      </>
    )
  }
}
