import React, { useState } from 'react';
      
export default function FormUsingFunComponent() {

  const[name,setName]=useState("")
  const[gender,setGender]=useState("")
  const[qulification,setQulification]=useState("")
    const [updated, setUpdated] = useState(name,gender,qulification);

const handleClick = () => {
    // ðŸ‘‡ "message" stores input field value
    setUpdated(name);
      setUpdated(gender);
      setUpdated(qulification);
  }
      
  return (
    <>
    <form>
       Full Name  :<input type="text" name="FullName" onChange={e => setName(e.target.value)}/><br />
        <label>Gender:</label>
        <input
            type="radio"
            value="male"
            name="gender"
            checked={gender === "male"}
            onChange={e => setGender(e.target.value)}

        />
        <label> Male:</label>
        <input
            type="radio"
            value="female" 
            name="gender"
            checked={gender === "female"}
            onChange={e => setGender(e.target.value)}
             />
        <label>Female</label><br/>
        <label>Qulification:</label>
        <select name="qulification" onChange={e => setQulification(e.target.value)} >
            <option value="Master of Computer Application">Master of Computer Application</option>
            <option value="Bachlore of Engineering">Bachlore of Engineering</option>
            <option value="B.Tech">B.Tech</option>
        </select>
        <p>Name:{name}<br/>Gender:{gender}<br/>Qulification:{qulification}</p>
        <button type="button" onClick={handleClick}>Submit</button>  
        
      <h2>Updated: {updated}</h2>
          </form>         
 </>
  )
}
