import React, { Component } from 'react'

export default class StateExample2 extends Component {
  constructor(props) {
    super(props)

    this.state = {
      number: '',
      result: '',
    }

    this.handleName = this.handleName.bind(this)
    this.Display = this.Display.bind(this)
  }

  handleName = (e) => {
    this.setState({ number: e.target.value });
  }




  Display = (e) => {
    let res = `Squre - ${this.state.number*2}`
   
    this.setState({ result: res });
  }

  render() {
    return (
      <>
        <form>
          Enter Number:<input type="text" name="txtName" onChange={this.handleName} />

          <button type="button" onClick={this.Display}>Click  here</button>
          <div>
            <p>{this.state.result}</p>
          </div>
        </form>
      </>
    )
  }
}
