import React, { Component } from 'react'

export default class AddandMulti extends Component {
  constructor() {
    super()
    this.state = {
      numberone: '',
      numbertwo:'',
      result: ''
    }

  }
 NumberFirst = (event) => {
    this.setState({ numberone: event.target.value });
  }
  NumberSecond = (event) => {
    this.setState({ numbertwo: event.target.value });
  }
  display = () => {
  
    let res = `Sum- ${parseInt(this.state.numberone) + parseInt(this.state.numbertwo)} ` //or ${this.state.number*2}
    this.setState({ result: res});
  }

  render() {
    return (

      <>
        <input type="text" name="numberone" onChange={this.NumberFirst} />
        <input type="text" name="numbertwo" onChange={this.NumberSecond} />
        <button onClick={this.display}>Submit</button>
        <p>{this.state.result}</p>
      </>
    )
  }
}
