import React, { Component } from 'react'

export default class FormUsingClassComponent extends Component {
    constructor(props) {
        super(props)

        this.state = {
            fullName: "",
            result: "",
            color: 'green'
        }


    }

    handleFullName = (e) => {
        this.setState({ fullName: e.target.value });
    }

    onRadioChange = (e) => {
        this.setState({
            color: e.target.value
        });
    }
    Display = (e) => {
        let res = `Name - ${this.state.fullName}  favorite color - ${this.state.color}`
        this.setState({ result: res });
    }

    render() {
        return (
            <>
                <form>
                    Name  :<input type="text" name="FullName" onChange={this.handleFullName} /><br />
                    <label>Gender:</label>
                
                       


                            <input
                                type="radio"
                                value="red"
                                checked={this.state.color === "red"}
                                onChange={this.onRadioChange}
                            />
                            <label> Male:</label>





                            <input
                                type="radio"
                                value="green"
                                checked={this.state.color === "green"}
                                onChange={this.onRadioChange}
                            />

                            <label>Female</label>


                     
                 
                    <button type="button" onClick={this.Display}>Click  here</button>

            
                        <p>{this.state.result}</p>
                   
                </form>
            </>
        )
    }
}
