import React, { Component } from 'react'

export default class AddandMulti extends Component {
  constructor() {
    super()
    this.state = {
      numberone: '',
      numbertwo:'',
      result: ''
    }

  }
 NumberFirst = (event) => {
    this.setState({ numberone: event.target.value });
  }
  NumberSecond = (event) => {
    this.setState({ numbertwo: event.target.value });
  }
  sum = () => {
  
    let res = `Sum- ${parseInt(this.state.numberone) + parseInt(this.state.numbertwo)} `
    this.setState({ result: res});
  }
  multiply = () => {
  
    let res = `Multuply- ${parseInt(this.state.numberone) * parseInt(this.state.numbertwo)} ` 
    this.setState({ result: res});
  }

  render() {
    return (
      <>
       First Number:<input type="text" name="numberone" onChange={this.NumberFirst} />
       Second Number:<input type="text" name="numbertwo" onChange={this.NumberSecond} /><br></br>
        <button onClick={this.sum}>Sum</button> 
         <button onClick={this.multiply}>Multiply</button>
        <p>{this.state.result}</p>
      </>
    )
  }
}
