import React, { Component } from 'react'

export default class StateExample2 extends Component {
  constructor(){
    super()
    this.state={
      number:'',
      result:''
    }

  }
  handleNumber=(e)=>{
    this.setState({number:e.target.value});
}
  display = ()=>{
    let res=`Number - ${this.state.number} `
        this.setState({result:res});
    
  }
  render() {
    return (
      
      <>
      <input type='text' name="number" onChange={this.handleNumber} />
      <button onClick={this.display}>Submit</button>
      <p>{this.state.result}</p>
      </>
    )
  }
}
