import React, { Component } from 'react'

export default class CountUsingClassComponent extends Component {
    constructor(props){
        super(props)
        this.state={
            count:0
        }
    }
    

  render() {
    return (
    <>
      <div>CountUsingClassComponent</div>
      <button onClick={()=>this.setState({count:this.state.count+1})}>Click</button>
      </>
    )
  }
}
