import React, { Component } from 'react'

export default class HelloFile extends Component {
  constructor(){
    super()
    this.state={
      msg:'Hello I am Here......',
      name:"Krishna Patidar"
    }
    
  }
  display =(e)=>{
    this.setState({
      msg:"Message Changed"
    })
  }
  nameChange = (e)=>{
    this.setState({
      name:"Kavita Patidar"
    })
  }

  render() {
    return (
      <>
        <h2>Practice</h2>
        <p>{this.state.name}</p> 
        <p>{this.state.msg}</p>
        <button onClick={this.display}>Click Me</button>
        <button onClick={this.nameChange}></button>

      </>
    )
  }
}
