import React, { useState } from 'react';
      
export default function FormUsingFunComponent() {
  const [name, setName] = useState('');
      
  function handleChange(e)  {
    setName(e.target.value);
  };
      
  return (
  
     <>
                <form>
                    Name  :<input type="text" name="FullName" onChange={this.handleFullName} /><br />
                    <label>Gender:</label>
                    <input
                        type="radio"
                        value="male"
                        checked={this.state.gender === "male"}
                        onChange={this.GenderHandler}
                    />
                    <label> Male:</label>
                    <input
                        type="radio"
                        value="female"
                        checked={this.state.gender === "female"}
                        onChange={this.GenderHandler}
                    />
                    <label>Female</label><br/><bbr/>
                    <label>Qulification:</label>
                    <select name="qulification" onChange={this.QulificationHandler}>
                        <option value="Master of Computer Application">Master of Computer Application</option>
                        <option value="Bachlore of Engineering">Bachlore of Engineering</option>
                        <option value="B.Tech">B.Tech</option>
                    </select>
                    <p>{this.state.result}</p>
                    <button type="button" onClick={this.Display}>Submit</button>
                </form>
            </>       
  );
}
