import React from 'react'
import { useState } from 'react'

export default function FormUsingFunComponent() {
    const [fullName,setName] =useState("")
   const handleName=(e)=>{
        setName({fullName:e.target.value});
    }
    const Display=()=>{
        let res=`Name - ${this.state.fullName}   --- Email - ${this.state.email} favorite color - ${this.state.color}`
        this.setState({result:res});
    }
    return (
        <>
            <form>
                <label for="fullName">Full Name:</label>
                <input type="text" name="fullName" onChange={handleName}></input><br />
                <label for="qulification">Qulification:</label>
                <select name="qulification" >
                    <option value="mca">Master Of Computer Application</option>
                    <option value="btech">B.Tech</option>
                    <option value="be">Bachlor of Engineering</option>
                </select><br />
                <label for="gender">Gender:</label>
                <input type="radio" name="gender" />
                <label for="male">Male</label>
                <input type="radio" name="gender" />
                <label for="female">Female</label><br />
                <button>submit</button>
                <p></p>


            </form>
        </>
    )
}
