import React, { Component } from 'react'

export default class StateExample2 extends Component {
    constructor(props){
        super(props)
        this.state = {
            number: ""
        }
        this.squre = this.squre.bind(this )

    } 
    squre=(e)=>{
        var sqr = this.setstate({number:e.target.value})
        var result =((this.sqr)*2)
      }
  render() {
    return (
      <>
      <input type="text" name="number" value={this.number}/>
      <button onClick={this.squre}>Squre</button>
      <p>Squre - {this.state.result}</p>
      </>
    )
  }
}
