import React from 'react'
import { useState } from 'react'

export default function FormUsingFunComponent() {

    const [fullName,setName] =useState("");


   const handleFullName = (event) => {
        this.setName({ fullName: event.target.value });
    }

   const Display = () => {
        setName(fullName) 
    }

    return (
        <>
            <form>
                Name  :<input type="text" name="FullName" onChange={handleFullName} /><br />
                
                <p>{this.state.result}</p>
                <button type="button" onClick={Display}>Submit</button>
            </form>
        </>
    )

}
