import React, { Component } from 'react'

export default class FormUsingClassComponent extends Component {
    constructor(props) {
        super(props)

        this.state = {
            fullName: "",
            gender: "male",
            qulification:"",
            result: ""
        }


    }

    handleFullName = (e) => {
        this.setState({ fullName: e.target.value });
    }

    RadioHandler = (e) => {
        this.setState({
            gender: e.target.value
        });
    }
    Display = (e) => {
        let res = `Name - ${this.state.fullName} Gender - ${this.state.gender}`
        this.setState({ result: res });
    }

    render() {
        return (
            <>
                <form>
                    Name  :<input type="text" name="FullName" onChange={this.handleFullName} /><br />
                    <label>Gender:</label>
                    <input
                        type="radio"
                        value="male"
                        checked={this.state.gender === "male"}
                        onChange={this.RadioHandler}
                    />
                    <label> Male:</label>
                    <input
                        type="radio"
                        value="female"
                        checked={this.state.gender === "female"}
                        onChange={this.RadioHandler}
                    />
                    <label>Female</label><br/><bbr/>
                    <label>Qulification:</label>
                    <select name="qulification">
                        <option value="mca">Master of Computer Application</option>
                        <option value="be">Bachlore of Engineering</option>
                        <option value="btech">B.Tech</option>
                    </select>
                    <p>{this.state.result}</p>
                    <button type="button" onClick={this.Display}>Submit</button>

                </form>
            </>
        )
    }
}
