import React from "react";


export default class StateExample2 extends React.Component {
 
  squareTheNumber = (event) => {
    event.preventDefault();
    
    const { number } = event.target.elements;
    alert(`this is the squared number: ${number.value ** 2}`);
  };

  render() {
    return (
      <div>
      
        <div>
          
            <input
              name="number"
              type="text"
              placeholder="Enter a number to square"
            />
            <button type="submit" onSubmit={this.squareTheNumber}>Submit me</button>
        
        </div>
        <div></div>
      </div>
    );
  }
}
