import React, { Component } from 'react'

export default class FormUsingClassComponent extends Component {
    constructor(props) {
        super(props)
    
        this.state = {
            fullName:"",
            email:"",
            result:"",
            color: 'green'
        }
    
        this.handleName=this.handleName.bind(this)
        this.handleEmail=this.handleEmail.bind(this)
        this.Display = this.Display.bind(this)
    }
    
    handleName=(e)=>{
        this.setState({fullName:e.target.value});
    }

    handleEmail=(e)=>{
        this.setState({email:e.target.value});
    }

    onRadioChange = (e) => {
        this.setState({
          color: e.target.value
        });
      }
    Display=(e)=>{
        let res=`Name - ${this.state.fullName}   --- Email - ${this.state.email} favorite color - ${this.state.color}`
        this.setState({result:res});
    }

  render() {
    return (
     <>
     <form>
        Name  :<input type="text" name="txtName"  onChange={this.handleName}/>
        Email : <input type="email" name="txtEmail" onChange={this.handleEmail}/>
        <p>Which is your favorit color</p>
       <div>
       <ul>
            <li>
              <label>
                <input
                  type="radio"
                  value="red"
                  checked={this.state.color === "red"}
                  onChange={this.onRadioChange}
                />
                <span>Red</span>
              </label>
            </li>
            <li>
              <label>
                <input
                  type="radio"
                  value="green"
                  checked={this.state.color === "green"}
                  onChange={this.onRadioChange}
                />
                <span>Green</span>
              </label>
            </li>
            <li>
              <label>
                <input
                  type="radio"
                  value="blue"
                  checked={this.state.color === "blue"}
                  onChange={this.onRadioChange}
                />
                <span>Blue</span>
              </label>
            </li>
            <li>
              <label>
                <input
                  type="radio"
                  value="orange"
                  checked={this.state.color === "orange"}
                  onChange={this.onRadioChange}
                />
                <span>Ornage</span>
              </label>
            </li>
            </ul>
       </div>
        <button type="button" onClick={this.Display}>Click  here</button>
        
        <div>
             <p>{this.state.result}</p>
      </div>
     </form>
     </>
    )
  }
}
