import React, { Component } from 'react'

export default class AddandMulti extends Component {
  constructor() {
    super()
    this.state = {
      number1: '',
      number2:'',
      result: ''
    }

  }
  SumNumber = (event) => {
    this.setState({ number1: event.target.value });
  }
  MultiNumber = (event) => {
    this.setState({ number2: event.target.value });
  }
  display = () => {
  
    let res = `Sum- ${this.state.number1 + this.state.number2} ` //or ${this.state.number*2}
    this.setState({ result: res});
  }

  render() {
    return (

      <>
        <input type="text" name="number1" onChange={this.SumNumber} />
        <input type="text" name="number2" onChange={this.SumNumber} />
        <button onClick={this.display}>Submit</button>
        <p>{this.state.result}</p>
      </>
    )
  }
}
