import React from 'react'
import { useState } from 'react'

export default function FormUsingFunComponent() {

    const [fullName,setName] =useState("");


    handleFullName = (event) => {
        this.setName({ fullName: event.target.value });
    }

    Display = () => {
        let res = `Name - ${this.state.fullName}`;
        this.setState({ result: res });
    }

    return (
        <>
            <form>
                Name  :<input type="text" name="FullName" onChange={this.handleFullName} /><br />
                
                <p>{this.state.result}</p>
                <button type="button" onClick={this.Display}>Submit</button>
            </form>
        </>
    )

}
