import React from "react";


export default class StateExample2 extends React.Component {
  constructor(props){
    super(props)
    this.result = {
        message:''
    }
  }
  squareTheNumber = (event) => {
    const { number } = event.target.elements;
     result =`this is the squared number: ${number.value ** 2}`;
  };

  render() {
    return (
      <div>
      
        <div>
          <form onSubmit={this.squareTheNumber}>
            <input
              name="number"
              type="text"
              placeholder="Enter a number to square"
            />
            <button type="submit">Submit me</button>

          </form>
          <p Result= {this.state.result}></p>
        </div>
        <div></div>
      </div>
    );
  }
}
